mapper.select:
select id, password, is_teacher as isTeacher, avatar, name, gender, phone, email, student_id as studentId, dormitory
from user
where id = #{id}

select *
from user
where id = #{id} and password = #{password}

mapper.update:
update user
set name=#{name}, gender=#{gender}, phone=#{phone}, email=#{email}, student_id=#{studentId}, dormitory=#{dormitory}
where id=#{id}
